function TaskA(){
    console.log("Doing work A!");
    next();
}

function TaskB(){
    console.log("Doing work B!");
    next();
}

function TaskC(){
    console.log("Doing work C!");
    next();
}


const tasks=[TaskA,TaskB,TaskC];

function next(err,result){
    if(err) throw err;
    const currentTask=tasks.shift();
    if(currentTask){
       currentTask(result);
    }
}

next();